#include <iostream>
#include <vector>
#include <queue>
#include <map>

using namespace std;

struct Die {
    int top, front;
    int x, y;
};

int main() {
    int n;
    while (cin >> n) {
        if (n == 0) break;
        vector<Die> dice(n);
        for (int i = 0; i < n; i++) {
            cin >> dice[i].top >> dice[i].front;
            dice[i].x = i;
            dice[i].y = 0;
        }

        map<int, int> count;
        for (int i = 0; i < 6; i++) {
            count[i] = 0;
        }

        queue<Die> q;
        for (int i = 0; i < n; i++) {
            q.push(dice[i]);
        }

        while (!q.empty()) {
            Die d = q.front();
            q.pop();

            if (d.y == 0) {
                count[d.top]++;
                count[d.front]++;
            }

            if (d.front == 6) {
                d.front = 4;
            } else if (d.front == 5) {
                d.front = 3;
            } else if (d.front == 4) {
                d.front = 2;
            } else if (d.front == 3) {
                d.front = 1;
            }

            if (d.front > d.top) {
                d.y++;
                q.push(d);
            }
        }

        for (int i = 1; i <= 6; i++) {
            cout << count[i] << " ";
        }
        cout << endl;
    }

    return 0;
}
