import java.util.*;
import java.io.*;

public class Main {
    static class Die {
        int top, front;
        Die left, right, up, down;

        public Die(int top, int front) {
            this.top = top;
            this.front = front;
        }

        public void roll() {
            if (top == 1 || top == 2 || top == 3) {
                if (front > top) {
                    if (left == null) {
                        left = new Die(top, front);
                    } else {
                        left.roll();
                    }
                } else if (front < top) {
                    if (right == null) {
                        right = new Die(top, front);
                    } else {
                        right.roll();
                    }
                }
                if (front > top) {
                    if (up == null) {
                        up = new Die(front, top);
                    } else {
                        up.roll();
                    }
                } else if (front < top) {
                    if (down == null) {
                        down = new Die(front, top);
                    } else {
                        down.roll();
                    }
                }
            } else {
                if (front > top) {
                    if (left == null) {
                        left = new Die(top, front);
                    } else {
                        left.roll();
                    }
                } else if (front < top) {
                    if (right == null) {
                        right = new Die(top, front);
                    } else {
                        right.roll();
                    }
                }
                if (front > top) {
                    if (up == null) {
                        up = new Die(front, top);
                    } else {
                        up.roll();
                    }
                } else if (front < top) {
                    if (down == null) {
                        down = new Die(front, top);
                    } else {
                        down.roll();
                    }
                }
            }
        }

        public void count() {
            int[] count = new int[6];
            if (left != null) {
                left.count();
                count[left.top - 1]++;
                count[left.front - 1]++;
            }
            if (right != null) {
                right.count();
                count[right.top - 1]++;
                count[right.front - 1]++;
            }
            if (up != null) {
                up.count();
                count[up.top - 1]++;
                count[up.front - 1]++;
            }
            if (down != null) {
                down.count();
                count[down.top - 1]++;
                count[down.front - 1]++;
            }
            System.out.print(count[0] + " ");
            for (int i = 1; i < 6; i++) {
                System.out.print(count[i] + " ");
            }
            System.out.println();
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        while (n != 0) {
            String[] input = br.readLine().split(" ");
            int top = Integer.parseInt(input[0]);
            int front = Integer.parseInt(input[1]);
            Die die = new Die(top, front);
            die.roll();
            die.count();
            n--;
        }
    }
}
