def roll_dice(t, f, dice):
    if t == 1 or t == 2 or t == 3:
        return dice
    else:
        if f == 1 or f == 2 or f == 3:
            return [t, f]
        else:
            if t > f:
                return [t, f]
            else:
                return [f, t]

def fall_dice(dice):
    new_dice = []
    for i in range(len(dice) - 1):
        new_dice.append(dice[i])
    return new_dice

def count_faces(dice):
    count = [0, 0, 0, 0, 0, 0]
    for i in range(len(dice)):
        count[dice[i][0] - 1] += 1
    return count

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        dice = []
        for i in range(n):
            t, f = map(int, input().split())
            dice.append(roll_dice(t, f, dice))
        while len(dice) > 1:
            dice = fall_dice(dice)
            for i in range(len(dice) - 1):
                dice[i] = roll_dice(dice[i][0], dice[i + 1][1], dice[i])
        count = count_faces(dice)
        print(*count)

main()
