#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

struct Station {
    int id;
    int distance;
    int company;
};

struct Company {
    int id;
    vector<int> distance;
    vector<int> fare;
};

struct Route {
    int totalFare;
    vector<int> path;
};

struct Compare {
    bool operator()(const pair<int, int>& a, const pair<int, int>& b) {
        return a.first > b.first;
    }
};

int main() {
    int n, m, c, s, g;
    cin >> n >> m >> c >> s >> g;

    vector<Station> stations(n + 1);
    for (int i = 1; i <= n; i++) {
        stations[i].id = i;
    }

    vector<Company> companies(c + 1);
    for (int i = 1; i <= c; i++) {
        companies[i].id = i;
    }

    vector<int> lineCount(n + 1);
    for (int i = 1; i <= m; i++) {
        int x, y, d, company;
        cin >> x >> y >> d >> company;
        stations[x].distance += d;
        stations[y].distance += d;
        lineCount[x]++;
        lineCount[y]++;
    }

    for (int i = 1; i <= c; i++) {
        int p;
        cin >> p;
        companies[i].distance.resize(p);
        for (int j = 0; j < p - 1; j++) {
            cin >> companies[i].distance[j];
        }
        companies[i].fare.resize(p);
        for (int j = 0; j < p; j++) {
            cin >> companies[i].fare[j];
        }
    }

    vector<int> distance(n + 1);
    vector<int> company(n + 1);
    vector<int> fare(n + 1);
    vector<int> path(n + 1);

    priority_queue<pair<int, int>, vector<pair<int, int>>, Compare> queue;
    queue.push({0, s});
    while (!queue.empty()) {
        int currentDistance = -queue.top().first;
        int currentNode = queue.top().second;
        queue.pop();
        if (currentNode == g) {
            break;
        }
        if (currentDistance > distance[currentNode]) {
            continue;
        }
        for (int i = 1; i <= lineCount[currentNode]; i++) {
            int nextNode = stations[currentNode].distance[i - 1] == stations[currentNode].distance[i] ? stations[currentNode].distance[i + 1] : stations[currentNode].distance[i];
            int nextDistance = currentDistance + stations[currentNode].distance[i];
            int nextCompany = companies[stations[currentNode].company[i]].id;
            int nextFare = 0;
            if (companies[stations[currentNode].company[i]].distance.size() > 0) {
                int index = lower_bound(companies[stations[currentNode].company[i]].distance.begin(), companies[stations[currentNode].company[i]].distance.end(), nextDistance) - companies[stations[currentNode].company[i]].distance.begin();
                if (index < companies[stations[currentNode].company[i]].distance.size()) {
                    nextFare = companies[stations[currentNode].company[i]].fare[index];
                }
            }
            if (nextDistance < distance[nextNode] || (nextDistance == distance[nextNode] && nextFare < fare[nextNode])) {
                distance[nextNode] = nextDistance;
                fare[nextNode] = nextFare;
                path[nextNode] = currentNode;
                queue.push({-nextDistance, nextNode});
            }
        }
    }

    if (distance[g] == numeric_limits<int>::max()) {
        cout << -1 << endl;
    } else {
        int currentNode = g;
        int totalFare = 0;
        while (currentNode != s) {
            int nextNode = path[currentNode];
            int nextDistance = distance[currentNode] - distance[nextNode];
            int nextCompany = companies[stations[currentNode].company[lineCount[currentNode] - 1]].id;
            int nextFare = 0;
            if (companies[stations[currentNode].company[lineCount[currentNode] - 1]].distance.size() > 0) {
                int index = lower_bound(companies[stations[currentNode].company[lineCount[currentNode] - 1]].distance.begin(), companies[stations[currentNode].company[lineCount[currentNode] - 1]].distance.end(), nextDistance) - companies[stations[currentNode].company[lineCount[currentNode] - 1]].distance.begin();
                if (index < companies[stations[currentNode].company[lineCount[currentNode] - 1]].distance.size()) {
                    nextFare = companies[stations[currentNode].company[lineCount[currentNode] - 1]].fare[index];
                }
            }
            totalFare += nextFare;
            currentNode = nextNode;
        }
        cout << totalFare << endl;
    }

    return 0;
}
