#include <stdio.h>
#include <math.h>

#define MAX_CIRCLES 100

typedef struct {
    double x, y, r;
} Circle;

double distance(Circle a, Circle b) {
    return sqrt(pow(a.x - b.x, 2) + pow(a.y - b.y, 2));
}

double shortest_path(Circle circles[], int n) {
    double dp[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (i == j) {
                dp[i][j] = 0;
            } else if (i + 1 == j) {
                dp[i][j] = distance(circles[i], circles[j]);
            } else {
                dp[i][j] = 1e9;
            }
        }
    }

    for (int k = 0; k < n; k++) {
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                dp[i][j] = fmin(dp[i][j], dp[i][k] + dp[k][j]);
            }
        }
    }

    return dp[0][n - 1];
}

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        Circle circles[n];
        for (int i = 0; i < n; i++) {
            scanf("%lf %lf %lf", &circles[i].x, &circles[i].y, &circles[i].r);
        }
        printf("%.6lf\n", shortest_path(circles, n));
    }
    return 0;
}
