import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNextInt()) {
            int n = scanner.nextInt();
            double[][] circles = new double[n][3];
            for (int i = 0; i < n; i++) {
                circles[i][0] = scanner.nextDouble();
                circles[i][1] = scanner.nextDouble();
                circles[i][2] = scanner.nextDouble();
            }
            System.out.println(calculatePathLength(circles));
        }
        scanner.close();
    }

    public static double calculatePathLength(double[][] circles) {
        double[][] distances = new double[circles.length][circles.length];
        for (int i = 0; i < circles.length; i++) {
            for (int j = 0; j < circles.length; j++) {
                if (i == j) {
                    distances[i][j] = 0;
                } else {
                    distances[i][j] = calculateDistance(circles[i], circles[j]);
                }
            }
        }

        double[][] dp = new double[circles.length][circles.length];
        for (int i = 0; i < circles.length; i++) {
            for (int j = 0; j < circles.length; j++) {
                dp[i][j] = Double.POSITIVE_INFINITY;
            }
        }
        dp[0][0] = 0;

        for (int k = 0; k < circles.length; k++) {
            for (int i = 0; i < circles.length; i++) {
                for (int j = 0; j < circles.length; j++) {
                    if (i != j && i != k && j != k) {
                        dp[i][j] = Math.min(dp[i][j], dp[i][k] + distances[k][j]);
                    }
                }
            }
        }

        return dp[0][circles.length - 1];
    }

    public static double calculateDistance(double[] circle1, double[] circle2) {
        double dx = circle1[0] - circle2[0];
        double dy = circle1[1] - circle2[1];
        return Math.sqrt(dx * dx + dy * dy);
    }
}
