import math

def distance(x1, y1, x2, y2):
    return math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

def intersect(x1, y1, r1, x2, y2, r2):
    d = distance(x1, y1, x2, y2)
    if d > r1 + r2:
        return False
    elif d < abs(r1 - r2):
        return False
    else:
        a = (r1**2 - r2**2 + d**2) / (2 * d)
        h = math.sqrt(r1**2 - a**2)
        xm = x1 + a * (x2 - x1) / d
        ym = y1 + a * (y2 - y1) / d
        xs1 = xm + h * (y2 - y1) / d
        xs2 = xm - h * (y2 - y1) / d
        ys1 = ym - h * (x2 - x1) / d
        ys2 = ym + h * (x2 - x1) / d
        return (distance(xs1, ys1, x2, y2) < r2 and
                distance(xs2, ys2, x2, y2) < r2)

def chain_confined_path(n, circles):
    path = [(circles[0][0], circles[0][1])]
    for i in range(1, n-1):
        if intersect(circles[i-1][0], circles[i-1][1], circles[i-1][2],
                    circles[i][0], circles[i][1], circles[i][2]):
            path.append((circles[i][0], circles[i][1]))
    path.append((circles[n-1][0], circles[n-1][1]))
    return path

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        circles = []
        for _ in range(n):
            x, y, r = map(float, input().split())
            circles.append((x, y, r))
        path = chain_confined_path(n, circles)
        length = 0
        for i in range(len(path) - 1):
            length += distance(path[i][0], path[i][1], path[i+1][0], path[i+1][1])
        print(f"{length:.6f}")

if __name__ == "__main__":
    main()
