#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MAXM 60
#define MAXN 7
#define MAXL 7
#define MAXP 100

typedef struct {
    int rank;
    int count;
} Card;

typedef struct {
    int rank;
    int plus;
} Pattern;

int read_int() {
    int x;
    scanf("%d", &x);
    return x;
}

void read_cards(Card cards[], int n, int m) {
    for (int i = 0; i < n * m; i++) {
        cards[i].rank = i % m + 1;
        cards[i].count = 1;
    }
}

void read_pattern(Pattern patterns[], int l) {
    for (int i = 0; i < l; i++) {
        char c;
        scanf(" %c", &c);
        if (c == '*') {
            patterns[i].rank = 0;
            patterns[i].plus = 0;
        } else {
            patterns[i].rank = c - 'a';
            scanf("%c", &c);
            while (c == '+') {
                patterns[i].plus++;
                scanf("%c", &c);
            }
        }
    }
}

double calculate_probability(Card cards[], int n, int m, Pattern patterns[], int l) {
    double total = 1.0;
    for (int i = 0; i < l; i++) {
        int rank = patterns[i].rank;
        int plus = patterns[i].plus;
        int count = 0;
        for (int j = 0; j < n * m; j++) {
            if (cards[j].rank == rank) {
                count++;
            }
        }
        double prob = 1.0;
        for (int j = 0; j <= plus; j++) {
            prob *= (double)count / (n * m);
            count--;
        }
        total *= prob;
    }
    return total;
}

int main() {
    Card cards[MAXM * MAXN];
    Pattern patterns[MAXL];
    int n, m, l;
    while (scanf("%d %d %d", &n, &m, &l) == 3) {
        if (n == 0 && m == 0 && l == 0) {
            break;
        }
        read_cards(cards, n, m);
        read_pattern(patterns, l);
        double prob = calculate_probability(cards, n, m, patterns, l);
        printf("%.10f\n", prob);
    }
    return 0;
}
