#include <stdio.h>
#include <stdlib.h>

#define MAX_H 100
#define MAX_W 100

int dfs(int h, int w, char grid[MAX_H][MAX_W], int visited[MAX_H][MAX_W]) {
    if (h < 0 || h >= MAX_H || w < 0 || w >= MAX_W || grid[h][w] == '.' || visited[h][w]) {
        return 0;
    }
    visited[h][w] = 1;
    return 1 + dfs(h - 1, w, grid, visited) + dfs(h + 1, w, grid, visited) + dfs(h, w - 1, grid, visited) + dfs(h, w + 1, grid, visited);
}

int main() {
    int h, w;
    while (scanf("%d %d", &h, &w) == 2) {
        if (h == 0 && w == 0) {
            break;
        }
        char grid[MAX_H][MAX_W];
        int visited[MAX_H][MAX_W];
        for (int i = 0; i < h; i++) {
            scanf("%s", grid[i]);
        }
        int count = 0;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                if (grid[i][j] == '#' && !visited[i][j]) {
                    count += dfs(i, j, grid, visited);
                }
            }
        }
        printf("%d\n", count);
    }
    return 0;
}
