#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int cutChocolate(vector<vector<char>>& grid) {
    int rows = grid.size();
    int cols = grid[0].size();
    vector<vector<int>> dp(rows, vector<int>(cols, 0));
    dp[0][0] = grid[0][0] == '#' ? 1 : 0;

    for (int i = 1; i < rows; i++) {
        if (grid[i][0] == '#') {
            dp[i][0] = dp[i - 1][0] + 1;
        }
    }

    for (int j = 1; j < cols; j++) {
        if (grid[0][j] == '#') {
            dp[0][j] = dp[0][j - 1] + 1;
        }
    }

    for (int i = 1; i < rows; i++) {
        for (int j = 1; j < cols; j++) {
            if (grid[i][j] == '#') {
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1]) + 1;
            }
        }
    }

    int maxArea = 0;
    for (int i = 0; i < rows; i++) {
        for (int j = 0; j < cols; j++) {
            if (grid[i][j] == '#') {
                maxArea = max(maxArea, dp[i][j]);
            }
        }
    }

    return maxArea;
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        int rows, cols;
        cin >> rows >> cols;
        vector<vector<char>> grid(rows, vector<char>(cols));
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                cin >> grid[i][j];
            }
        }
        cout << cutChocolate(grid) << endl;
    }
    return 0;
}
