def count_pieces(h, w, grid):
    visited = [[False for _ in range(w)] for _ in range(h)]
    pieces = 0

    def dfs(i, j):
        if i < 0 or i >= h or j < 0 or j >= w or not grid[i][j] or visited[i][j]:
            return
        visited[i][j] = True
        dfs(i - 1, j)
        dfs(i + 1, j)
        dfs(i, j - 1)
        dfs(i, j + 1)

    for i in range(h):
        for j in range(w):
            if grid[i][j] and not visited[i][j]:
                dfs(i, j)
                pieces += 1

    return pieces


def main():
    while True:
        h, w = map(int, input().split())
        if h == 0 and w == 0:
            break
        grid = []
        for _ in range(h):
            grid.append(list(input()))
        print(count_pieces(h, w, grid))


if __name__ == "__main__":
    main()
