#include <iostream>
#include <vector>
#include <map>
#include <algorithm>

using namespace std;

struct Submission {
    int time;
    int team;
    int problem;
    int judgment;
};

struct Team {
    int team;
    int problems;
    int time;
};

bool compareTeams(const Team& a, const Team& b) {
    if (a.problems != b.problems) {
        return a.problems > b.problems;
    } else if (a.time != b.time) {
        return a.time < b.time;
    } else {
        return a.team < b.team;
    }
}

int main() {
    int m, t, p, r;
    cin >> m >> t >> p >> r;

    vector<Submission> submissions(r);
    for (int i = 0; i < r; i++) {
        cin >> submissions[i].time >> submissions[i].team >> submissions[i].problem >> submissions[i].judgment;
    }

    map<int, Team> teams;
    for (int i = 0; i < r; i++) {
        if (submissions[i].judgment == 0) {
            if (teams.find(submissions[i].team) == teams.end()) {
                teams[submissions[i].team] = {submissions[i].team, 1, submissions[i].time};
            } else {
                teams[submissions[i].team].problems++;
                teams[submissions[i].team].time += submissions[i].time - submissions[i-1].time;
            }
        }
    }

    vector<Team> teamList;
    for (auto& team : teams) {
        teamList.push_back(team.second);
    }

    sort(teamList.begin(), teamList.end(), compareTeams);

    for (int i = 0; i < teamList.size(); i++) {
        if (i > 0 && teamList[i].problems == teamList[i-1].problems && teamList[i].time == teamList[i-1].time) {
            cout << teamList[i].team << "=";
        } else {
            cout << teamList[i].team;
        }
        if (i < teamList.size() - 1) {
            cout << ",";
        }
    }
    cout << endl;

    return 0;
}
