import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] line;
        while ((line = br.readLine().split(" ")) != null && line.length > 0) {
            int M = Integer.parseInt(line[0]);
            int T = Integer.parseInt(line[1]);
            int P = Integer.parseInt(line[2]);
            int R = Integer.parseInt(line[3]);
            Map<Integer, Map<Integer, Integer>> team = new HashMap<>();
            for (int i = 0; i < R; i++) {
                line = br.readLine().split(" ");
                int mk = Integer.parseInt(line[0]);
                int tk = Integer.parseInt(line[1]);
                int pk = Integer.parseInt(line[2]);
                int jk = Integer.parseInt(line[3]);
                if (!team.containsKey(tk)) {
                    team.put(tk, new HashMap<>());
                }
                if (jk == 0) {
                    team.get(tk).put(pk, mk);
                } else {
                    if (!team.get(tk).containsKey(pk)) {
                        team.get(tk).put(pk, 0);
                    }
                    team.get(tk).put(pk, Math.max(team.get(tk).get(pk), mk + 20 * (team.get(tk).get(pk) / 20 + 1)));
                }
            }
            List<Map.Entry<Integer, Integer>> list = new ArrayList<>();
            for (Map.Entry<Integer, Integer> entry : team.entrySet()) {
                int count = 0;
                int time = 0;
                for (int value : entry.getValue().values()) {
                    count++;
                    time += value;
                }
                list.add(new AbstractMap.SimpleEntry<>(entry.getKey(), count));
                list.add(new AbstractMap.SimpleEntry<>(entry.getKey(), time));
            }
            Collections.sort(list, (o1, o2) -> {
                if (o1.getValue() == o2.getValue()) {
                    if (o1.getKey() == o2.getKey()) {
                        return 0;
                    } else {
                        return o2.getKey() - o1.getKey();
                    }
                } else {
                    return o2.getValue() - o1.getValue();
                }
            });
            StringBuilder sb = new StringBuilder();
            int prev = -1;
            for (Map.Entry<Integer, Integer> entry : list) {
                if (entry.getValue() instanceof Integer) {
                    if (prev != entry.getValue()) {
                        if (prev != -1) {
                            sb.append(",");
                        }
                        prev = entry.getValue();
                        sb.append(entry.getKey());
                    } else {
                        if (prev != -1) {
                            sb.append("=");
                        }
                        prev = entry.getValue();
                        sb.append(entry.getKey());
                    }
                } else {
                    prev = -1;
                }
            }
            System.out.println(sb.toString());
        }
    }
}
