#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_LENGTH 10000

int min_votes(char *str) {
    int i, j, k, min = 0, max = 0;
    int len = strlen(str);
    int *votes = (int *)malloc((len + 1) * sizeof(int));
    int *max_votes = (int *)malloc((len + 1) * sizeof(int));
    int *min_votes = (int *)malloc((len + 1) * sizeof(int));

    for (i = 0; i <= len; i++) {
        votes[i] = 0;
        max_votes[i] = 0;
        min_votes[i] = 0;
    }

    for (i = 0; i < len; i++) {
        if (str[i] == '[') {
            continue;
        } else if (str[i] == ']') {
            continue;
        } else if (str[i] == ',') {
            continue;
        } else {
            votes[i] = str[i] - '0';
            max_votes[i] = str[i] - '0';
            min_votes[i] = str[i] - '0';
        }
    }

    for (i = 0; i < len; i++) {
        if (str[i] == '[') {
            continue;
        } else if (str[i] == ']') {
            continue;
        } else if (str[i] == ',') {
            continue;
        } else {
            for (j = i; j < len; j++) {
                if (str[j] == '[') {
                    break;
                } else if (str[j] == ']') {
                    break;
                } else if (str[j] == ',') {
                    break;
                } else {
                    votes[j] += votes[i];
                    max_votes[j] = max_votes[j] > votes[j] ? max_votes[j] : votes[j];
                    min_votes[j] = min_votes[j] < votes[j] ? min_votes[j] : votes[j];
                }
            }
        }
    }

    for (i = 0; i < len; i++) {
        if (str[i] == '[') {
            continue;
        } else if (str[i] == ']') {
            continue;
        } else if (str[i] == ',') {
            continue;
        } else {
            if (max_votes[i] - min_votes[i] + 1 <= (votes[i] + 1) / 2) {
                min = min_votes[i];
                break;
            }
        }
    }

    free(votes);
    free(max_votes);
    free(min_votes);

    return min;
}

int main() {
    int n;
    char str[MAX_LENGTH];

    scanf("%d", &n);
    while (n--) {
        scanf("%s", str);
        printf("%d\n", min_votes(str));
    }

    return 0;
}
