#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

int minVotes(const std::string& district) {
    int minVotes = 0;
    int maxVotes = 0;
    int sum = 0;
    int count = 0;

    for (char c : district) {
        if (c == '[') {
            continue;
        } else if (c == ']') {
            if (count % 2 == 1) {
                sum += maxVotes;
                maxVotes = 0;
            }
            count = 0;
        } else {
            count++;
            int num = c - '0';
            while (c + 1 < district.size() && district[c + 1] >= '0' && district[c + 1] <= '9') {
                num = num * 10 + (district[c + 1] - '0');
                c++;
            }
            sum += num;
            maxVotes = std::max(maxVotes, num);
        }
    }

    if (count % 2 == 1) {
        sum += maxVotes;
    }

    return sum;
}

int main() {
    int n;
    std::cin >> n;

    for (int i = 0; i < n; i++) {
        std::string district;
        std::cin >> district;
        std::cout << minVotes(district) << std::endl;
    }

    return 0;
}
