import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int t = scanner.nextInt();
        scanner.nextLine();

        for (int i = 0; i < t; i++) {
            String s = scanner.nextLine();
            System.out.println(minVotes(s));
        }
    }

    public static int minVotes(String s) {
        int min = 0;
        int n = s.length();
        int i = 0;

        while (i < n) {
            if (s.charAt(i) == '[') {
                i++;
                int j = i;
                while (s.charAt(j) != ']') {
                    j++;
                }
                int sub = Integer.parseInt(s.substring(i, j));
                min += (sub + 1) / 2;
                i = j + 1;
            } else {
                int k = i;
                while (s.charAt(k) != ']') {
                    k++;
                }
                int sub = Integer.parseInt(s.substring(i, k));
                min += (sub + 1) / 2;
                i = k + 1;
            }
        }

        return min;
    }
}
