#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

bool isPrime(int num) {
    if (num <= 1) return false;
    if (num <= 3) return true;
    if (num % 2 == 0 || num % 3 == 0) return false;
    for (int i = 5; i * i <= num; i += 6)
        if (num % i == 0 || num % (i + 2) == 0)
            return false;
    return true;
}

void dfs(int m, int n, std::vector<std::vector<int>>& cave, std::vector<std::vector<int>>& dp, int& maxPrime, int& lastPrime) {
    if (n < 1 || n > m) return;
    if (isPrime(n)) {
        if (dp[n][0] == 0) {
            maxPrime++;
            lastPrime = n;
        }
        dp[n][0] = maxPrime;
        dp[n][1] = lastPrime;
    }
    if (n + m >= cave.size()) return;
    if (dp[n + m][0] == 0) {
        dfs(m, n + m + 1, cave, dp, maxPrime, lastPrime);
        dfs(m, n + m - 1, cave, dp, maxPrime, lastPrime);
        dfs(m, n + m + m, cave, dp, maxPrime, lastPrime);
    }
    if (dp[n + m][0] > 0) {
        if (dp[n + m][0] > maxPrime) {
            maxPrime = dp[n + m][0];
            lastPrime = dp[n + m][1];
        }
    }
}

int main() {
    int m, n;
    while (std::cin >> m >> n) {
        if (m == 0 && n == 0) break;
        std::vector<std::vector<int>> cave;
        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= i; j++) {
                cave.push_back({i, j});
            }
        }
        std::vector<std::vector<int>> dp(cave.size(), std::vector<int>(2, 0));
        int maxPrime = 0;
        int lastPrime = 0;
        dfs(m, n, cave, dp, maxPrime, lastPrime);
        std::cout << maxPrime << " " << lastPrime << std::endl;
    }
    return 0;
}
