import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int m = scanner.nextInt();
            int n = scanner.nextInt();
            if (m == 0 && n == 0) break;
            int[][] caves = new int[m][m];
            for (int i = 0; i < m; i++) {
                for (int j = 0; j < m; j++) {
                    caves[i][j] = -1;
                }
            }
            int[] primes = sieveOfEratosthenes(m * m);
            int[] directions = {0, 1, -1, 1, -1, 0, 1, -1};
            int[] visited = new int[m * m];
            int[] queue = new int[m * m];
            int front = 0;
            int rear = 0;
            queue[rear++] = n - 1;
            visited[n - 1] = 1;
            int maxCount = 0;
            int maxPrime = 0;
            while (front < rear) {
                int index = queue[front++];
                int count = 0;
                int prime = -1;
                for (int i = 0; i < 4; i++) {
                    int x = index / m + directions[i];
                    int y = index % m + directions[i + 4];
                    if (x >= 0 && x < m && y >= 0 && y < m && caves[x][y] == -1) {
                        caves[x][y] = primes[index];
                        queue[rear++] = x * m + y;
                        visited[x * m + y] = 1;
                        if (primes[index] != 0) {
                            count++;
                            prime = primes[index];
                        }
                    }
                }
                if (count > maxCount || (count == maxCount && prime > maxPrime)) {
                    maxCount = count;
                    maxPrime = prime;
                }
            }
            System.out.println(maxCount + " " + maxPrime);
        }
    }

    public static int[] sieveOfEratosthenes(int n) {
        boolean[] isPrime = new boolean[n + 1];
        for (int i = 0; i <= n; i++) {
            isPrime[i] = true;
        }
        isPrime[0] = isPrime[1] = false;
        for (int i = 2; i * i <= n; i++) {
            if (isPrime[i]) {
                for (int j = i * i; j <= n; j += i) {
                    isPrime[j] = false;
                }
            }
        }
        int count = 0;
        for (int i = 2; i <= n; i++) {
            if (isPrime[i]) count++;
        }
        int[] primes = new int[count];
        int index = 0;
        for (int i = 2; i <= n; i++) {
            if (isPrime[i]) primes[index++] = i;
        }
        return primes;
    }
}
