#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

struct Point {
    double x, y;
};

struct Rope {
    Point p;
    double l;
};

double distance(const Point& p1, const Point& p2) {
    return std::sqrt(std::pow(p1.x - p2.x, 2) + std::pow(p1.y - p2.y, 2));
}

double height(const std::vector<Rope>& ropes, double h) {
    for (const auto& rope : ropes) {
        double d = distance(rope.p, {rope.p.x, rope.p.y - h});
        if (d > rope.l) return -1;
    }
    return h;
}

int main() {
    int n;
    while (std::cin >> n && n != 0) {
        std::vector<Rope> ropes(n);
        for (auto& rope : ropes) {
            std::cin >> rope.p.x >> rope.p.y >> rope.l;
        }
        double h = 0;
        while (true) {
            double h1 = height(ropes, h + 1);
            if (h1 == -1) break;
            h = h1;
        }
        std::cout << std::fixed << std::setprecision(10) << h << std::endl;
    }
    return 0;
}
