#include <iostream>
#include <vector>
#include <string>
#include <sstream>
#include <unordered_map>

using namespace std;

const int MAXN = 30;
const int MAXK = 10;

struct Rule {
    int k;
    vector<int> x;
    int y;
};

bool dfs(vector<int>& a, vector<int>& b, vector<Rule>& rules, int n, int m, int len, unordered_map<string, int>& memo) {
    string key = to_string(n) + "," + to_string(m) + "," + to_string(len);
    if (memo.find(key) != memo.end()) return memo[key] == 1;
    if (n == 0 && m == 0) return len == 0;
    if (n == 0 || m == 0) return false;

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            vector<int> a1 = a;
            vector<int> b1 = b;
            a1.insert(a1.begin() + i, a1.back());
            a1.pop_back();
            b1.insert(b1.begin() + j, b1.back());
            b1.pop_back();

            bool found = false;
            for (Rule& rule : rules) {
                if (a1.size() >= rule.k && b1.size() >= rule.k) {
                    bool match = true;
                    for (int k = 0; k < rule.k; k++) {
                        if (a1[i + k] != rule.x[k]) {
                            match = false;
                            break;
                        }
                    }
                    if (match) {
                        a1.erase(a1.begin() + i, a1.begin() + i + rule.k);
                        b1.erase(b1.begin() + j, b1.begin() + j + rule.k);
                        a1.push_back(rule.y);
                        b1.push_back(rule.y);
                        found = true;
                        break;
                    }
                }
            }

            if (found) {
                if (dfs(a1, b1, rules, n, m, len, memo)) {
                    memo[key] = 1;
                    return true;
                }
            }
        }
    }

    memo[key] = 0;
    return false;
}

int main() {
    int n, m, r;
    while (cin >> n >> m >> r) {
        if (n == 0 && m == 0 && r == 0) break;

        vector<int> a(n);
        for (int i = 0; i < n; i++) cin >> a[i];

        vector<int> b(m);
        for (int i = 0; i < m; i++) cin >> b[i];

        vector<Rule> rules(r);
        for (int i = 0; i < r; i++) {
            cin >> rules[i].k;
            rules[i].x.resize(rules[i].k);
            for (int j = 0; j < rules[i].k; j++) cin >> rules[i].x[j];
            cin >> rules[i].y;
        }

        unordered_map<string, int> memo;
        if (dfs(a, b, rules, n, m, max(n, m), memo)) {
            cout << max(n, m) << endl;
        } else {
            cout << -1 << endl;
        }
    }

    return 0;
}
