from collections import deque

def rotate(seq):
    return seq[1:] + seq[:1]

def rewrite(seq, rule):
    k, *x, y = rule
    for i in range(len(seq) - k + 1):
        if seq[i:i+k] == x:
            return seq[:i] + [y] + seq[i+k:]
    return seq

def solve(n, m, r, A, B, rules):
    queue = deque([(A, B, 0)])
    visited = set((tuple(A), tuple(B)))
    while queue:
        a, b, step = queue.popleft()
        if a == b:
            return max(len(a), len(b))
        for i in range(len(a)):
            a = rotate(a)
            if (tuple(a), tuple(b)) not in visited:
                queue.append((a, b, step + 1))
                visited.add((tuple(a), tuple(b)))
        for rule in rules:
            a = rewrite(a, rule)
            if (tuple(a), tuple(b)) not in visited:
                queue.append((a, b, step + 1))
                visited.add((tuple(a), tuple(b)))
    return -1

def main():
    while True:
        n, m, r = map(int, input().split())
        if n == 0 and m == 0 and r == 0:
            break
        A = list(map(int, input().split()))
        B = list(map(int, input().split()))
        rules = []
        for _ in range(r):
            k, *x, y = map(int, input().split())
            rules.append((k, x, y))
        print(solve(n, m, r, A, B, rules))

if __name__ == "__main__":
    main()
