import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        while (true) {
            int x = scanner.nextInt();
            int y = scanner.nextInt();
            int s = scanner.nextInt();

            if (x == 0 && y == 0 && s == 0) {
                break;
            }

            int maxAfterTaxPrice = 0;

            for (int i = 1; i < s; i++) {
                int j = s - i;
                int afterTaxPriceWithX = (int) Math.round((i * 100 + x * i) / 100.0);
                int afterTaxPriceWithY = (int) Math.round((j * 100 + y * j) / 100.0);
                int afterTaxPriceWithY2 = (int) Math.round((i * 100 + y * i) / 100.0);
                int afterTaxPriceWithY3 = (int) Math.round((j * 100 + y * j) / 100.0);

                if (afterTaxPriceWithX + afterTaxPriceWithY > maxAfterTaxPrice) {
                    maxAfterTaxPrice = afterTaxPriceWithX + afterTaxPriceWithY;
                }
                if (afterTaxPriceWithY2 + afterTaxPriceWithY3 > maxAfterTaxPrice) {
                    maxAfterTaxPrice = afterTaxPriceWithY2 + afterTaxPriceWithY3;
                }
            }

            System.out.println(maxAfterTaxPrice);
        }
    }
}
