import math

def calculate_after_tax_price(x, y, s):
    max_after_tax_price = 0
    for i in range(1, s):
        for j in range(i, s):
            if i + j == s:
                after_tax_price_i = (i * (100 + x)) / 100
                after_tax_price_j = (j * (100 + x)) / 100
                after_tax_price_i_new = (i * (100 + y)) / 100
                after_tax_price_j_new = (j * (100 + y)) / 100
                max_after_tax_price = max(max_after_tax_price, math.ceil(after_tax_price_i_new + after_tax_price_j_new))
    return max_after_tax_price

def main():
    while True:
        x, y, s = map(int, input().split())
        if x == 0 and y == 0 and s == 0:
            break
        print(calculate_after_tax_price(x, y, s))

if __name__ == "__main__":
    main()
