#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int disappear(vector<vector<int>>& board) {
    int score = 0;
    for (int i = 0; i < board.size(); i++) {
        for (int j = 0; j < board[i].size(); j++) {
            if (j < board[i].size() - 2 && board[i][j] == board[i][j + 1] && board[i][j] == board[i][j + 2]) {
                score += board[i][j];
                for (int k = j; k < board[i].size() - 2; k++) {
                    board[i][k] = 0;
                }
            }
        }
    }
    for (int i = 0; i < board.size(); i++) {
        for (int j = 0; j < board[i].size(); j++) {
            if (board[i][j] != 0) {
                for (int k = j + 1; k < board[i].size(); k++) {
                    if (board[i][k] != 0) {
                        board[i][k - 1] = board[i][k];
                        board[i][k] = 0;
                    }
                }
            }
        }
    }
    return score;
}

int main() {
    int h;
    cin >> h;
    while (h != 0) {
        vector<vector<int>> board(h, vector<int>(5));
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < 5; j++) {
                cin >> board[i][j];
            }
        }
        int score = 0;
        while (true) {
            int temp = disappear(board);
            if (temp == 0) {
                break;
            }
            score += temp;
        }
        cout << score << endl;
        cin >> h;
    }
    return 0;
}
