#include <stdio.h>
#include <math.h>

typedef struct {
    int x1, x2, h;
} Silhouette;

int compareSilhouettes(const void *a, const void *b) {
    Silhouette *sil1 = (Silhouette *)a;
    Silhouette *sil2 = (Silhouette *)b;
    return (sil1->x2 - sil2->x2);
}

int main() {
    int r, n, i, j;
    double t, t1, t2, t3, t4;
    Silhouette silhouettes[100];

    while (scanf("%d %d", &r, &n) == 2) {
        if (n == 0) break;

        for (i = 0; i < n; i++) {
            scanf("%d %d %d", &silhouettes[i].x1, &silhouettes[i].x2, &silhouettes[i].h);
        }

        qsort(silhouettes, n, sizeof(Silhouette), compareSilhouettes);

        t = 0;
        for (i = 0; i < n; i++) {
            t1 = (silhouettes[i].x1 + silhouettes[i].x2) / 2.0;
            t2 = (silhouettes[i].x2 - silhouettes[i].x1) / 2.0;
            t3 = (silhouettes[i].h + r) / 1.0;
            t4 = (silhouettes[i].h - r) / 1.0;

            if (t3 > t4) {
                t = max(t, t3);
            } else {
                t = max(t, t4);
            }
        }

        printf("%.4lf\n", t);
    }

    return 0;
}
