#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

struct Rectangle {
    double x1, x2, h;
};

bool compareRectangles(const Rectangle& a, const Rectangle& b) {
    return a.x1 < b.x1;
}

double calculateTime(int r, int n, std::vector<Rectangle>& rectangles) {
    std::sort(rectangles.begin(), rectangles.end(), compareRectangles);

    double time = 0;
    double sunY = -r;

    for (int i = 0; i < n; ++i) {
        double x1 = rectangles[i].x1;
        double x2 = rectangles[i].x2;
        double h = rectangles[i].h;

        if (sunY > h) {
            break;
        }

        double intersectionX1 = std::max(x1, -r + time);
        double intersectionX2 = std::min(x2, r + time);

        if (intersectionX1 <= intersectionX2) {
            sunY = std::max(sunY, h);
            time = (intersectionX2 - x1 + r - sunY) / 1;
        }
    }

    return time;
}

int main() {
    int r, n;
    while (std::cin >> r >> n) {
        if (r == 0 && n == 0) {
            break;
        }

        std::vector<Rectangle> rectangles(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> rectangles[i].x1 >> rectangles[i].x2 >> rectangles[i].h;
        }

        double time = calculateTime(r, n, rectangles);
        std::cout << std::fixed << std::setprecision(4) << time << std::endl;
    }

    return 0;
}
