import math

def get_intersection(x1, x2, y1, y2, x3, y3):
    if y1 <= y3:
        return None
    if y2 <= y3:
        return None
    if x1 == x2:
        return None
    m = (y3 - y1) / (y1 - y2)
    x = (x3 - x1) / (m - 1)
    if x < x1 or x > x2:
        return None
    return x

def get_time(r, silhouettes):
    silhouettes.sort(key=lambda x: x[0])
    time = 0
    while True:
        intersection = None
        for i in range(len(silhouettes) - 1):
            x1, x2, y1 = silhouettes[i]
            x3, x4, y2 = silhouettes[i + 1]
            intersection = get_intersection(x1, x2, 0, y1, x3, y2)
            if intersection is not None:
                break
        if intersection is None:
            return time
        intersection = max(intersection, silhouettes[0][0] - r)
        intersection = min(intersection, silhouettes[-1][1] + r)
        if intersection < 0:
            return time
        time = intersection + r
        for i in range(len(silhouettes)):
            x, x1, y = silhouettes[i]
            if x + r > intersection:
                silhouettes[i] = (x, x1, y)
            else:
                silhouettes[i] = (x + r, x1 + r, y)

def main():
    while True:
        r, n = map(int, input().split())
        if r == 0 and n == 0:
            break
        silhouettes = []
        for _ in range(n):
            x, x1, y = map(int, input().split())
            silhouettes.append((x, x1, y))
        print(f"{get_time(r, silhouettes):.4f}")

if __name__ == "__main__":
    main()
