void swap(char *a, char *b) {
    char temp = *a;
    *a = *b;
    *b = temp;
}

void sort(char *s, int l, int r) {
    while (l < r) {
        int i = l, j = l, k = r;
        char pivot = s[l + (r - l) / 2];

        while (j <= k) {
            if (s[j] == pivot) {
                j++;
            } else if (s[j] < pivot) {
                swap(&s[i], &s[j]);
                i++;
                j++;
            } else {
                swap(&s[j], &s[k]);
                k--;
            }
        }

        sort(s, l, i - 1);
        l = k + 1;
    }
}

