#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

void solve(const std::string& s) {
    std::vector<std::string> candidates;
    std::string t = s;
    for (int i = 'b'; i <= 'z'; ++i) {
        while (t.size() > 0 && t[0] == i + 1) {
            t.erase(0, 1);
        }
        if (t.size() > 0 && t[0] == (char)i) {
            t[0] = (char)(i - 1);
        }
        candidates.push_back(t);
    }
    std::sort(candidates.begin(), candidates.end());
    int n = candidates.size();
    if (n <= 10) {
        for (const auto& c : candidates) {
            std::cout << c << std::endl;
        }
    } else {
        for (int i = 0; i < 5; ++i) {
            std::cout << candidates[i] << std::endl;
        }
        for (int i = n - 5; i < n; ++i) {
            std::cout << candidates[i] << std::endl;
        }
    }
    std::cout << n << std::endl;
}

int main() {
    std::string s;
    while (std::getline(std::cin, s)) {
        if (s == "#") {
            break;
        }
        solve(s);
    }
    return 0;
}
