#include <stdio.h>
#include <stdlib.h>

#define MAX_N 1000
#define INF 1000000000

int n;
int p[MAX_N], d[MAX_N];
int dp[MAX_N][MAX_N];
int parent[MAX_N];

void dfs(int u) {
    for (int v = 0; v < n; v++) {
        if (dp[u][v] != INF) {
            parent[v] = u;
            dfs(v);
        }
    }
}

int main() {
    int t;
    scanf("%d", &t);
    while (t--) {
        scanf("%d", &n);
        for (int i = 1; i < n; i++) {
            scanf("%d", &p[i]);
            p[i]--;
        }
        for (int i = 1; i < n; i++) {
            scanf("%d", &d[i]);
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                dp[i][j] = INF;
            }
        }
        for (int i = 0; i < n; i++) {
            dp[i][i] = 0;
        }
        for (int i = 1; i < n; i++) {
            dp[p[i]][i] = d[i];
        }
        for (int k = 0; k < n; k++) {
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    dp[i][j] = (dp[i][j] < dp[i][k] + dp[k][j] + d[j]) ? dp[i][j] : dp[i][k] + dp[k][j] + d[j];
                }
            }
        }
        int min = INF;
        for (int i = 0; i < n; i++) {
            min = (min < dp[i][i]) ? min : dp[i][i];
        }
        printf("%d\n", min);
    }
    return 0;
}
