#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int MAXN = 805;
const int INF = numeric_limits<int>::max();

int n;
vector<int> p[MAXN];
vector<int> d[MAXN];
int dp[MAXN][MAXN];
int dist[MAXN][MAXN];

void init() {
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            dp[i][j] = INF;
            dist[i][j] = INF;
        }
    }
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        cin >> n;
        for (int i = 1; i <= n; i++) {
            p[i].clear();
            d[i].clear();
        }
        for (int i = 1; i <= n; i++) {
            p[i].push_back(0);
            d[i].push_back(0);
        }
        for (int i = 2; i <= n; i++) {
            int x;
            cin >> x;
            p[x].push_back(i);
        }
        for (int i = 2; i <= n; i++) {
            cin >> d[i][p[i].back()];
        }
        init();
        for (int i = 1; i <= n; i++) {
            dp[i][i] = 0;
            dist[i][i] = 0;
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j < p[i].size(); j++) {
                int x = p[i][j];
                dp[i][x] = d[i][x];
                dist[i][x] = d[i][x];
            }
        }
        for (int k = 1; k <= n; k++) {
            for (int i = 1; i <= n; i++) {
                for (int j = 1; j <= n; j++) {
                    if (i != j) {
                        dp[i][j] = min(dp[i][j], dp[i][k] + dp[k][j]);
                        dist[i][j] = min(dist[i][j], dist[i][k] + dist[k][j]);
                    }
                }
            }
        }
        int ans = INF;
        for (int i = 1; i <= n; i++) {
            ans = min(ans, dp[i][i] + dist[i][i]);
        }
        cout << ans << endl;
    }
    return 0;
}
