#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX 100000

int dx[4] = {0, 1, 0, -1};
int dy[4] = {1, 0, -1, 0};

int dfs(int x, int y, int t[6], int p, int q, char *s, int *cnt, int *used) {
    if (p > q) return 1;
    if (t[x] + t[y] < *cnt) return 0;
    if (t[x] + t[y] == *cnt && s[*cnt] != '\0') return 0;
    if (s[*cnt] != '\0' && s[*cnt] != 'N' && s[*cnt] != 'S' && s[*cnt] != 'E' && s[*cnt] != 'W') return 0;
    if (s[*cnt] == 'N' && x == 0) return 0;
    if (s[*cnt] == 'S' && x == 3) return 0;
    if (s[*cnt] == 'E' && y == 1) return 0;
    if (s[*cnt] == 'W' && y == 2) return 0;
    *cnt = t[x] + t[y];
    s[*cnt] = 'N';
    used[x] = 1;
    if (dfs(0, y, t, p, q, s, cnt, used)) return 1;
    s[*cnt] = '\0';
    used[x] = 0;
    s[*cnt] = 'S';
    used[x] = 1;
    if (dfs(3, y, t, p, q, s, cnt, used)) return 1;
    s[*cnt] = '\0';
    used[x] = 0;
    s[*cnt] = 'E';
    used[y] = 1;
    if (dfs(x, 1, t, p, q, s, cnt, used)) return 1;
    s[*cnt] = '\0';
    used[y] = 0;
    s[*cnt] = 'W';
    used[y] = 1;
    if (dfs(x, 2, t, p, q, s, cnt, used)) return 1;
    s[*cnt] = '\0';
    used[y] = 0;
    return 0;
}

int main() {
    int t[6], p, q;
    char s[MAX];
    int cnt = 0;
    int used[4] = {0};
    int i, j, k, l, m, n, o, x, y, z;
    while (scanf("%d %d %d %d %d %d %d %d", &t[0], &t[1], &t[2], &t[3], &t[4], &t[5], &p, &q) == 8) {
        if (t[0] == 0 && t[1] == 0 && t[2] == 0 && t[3] == 0 && t[4] == 0 && t[5] == 0) break;
        memset(s, 0, sizeof(s));
        memset(used, 0, sizeof(used));
        if (dfs(0, 1, t, p, q, s, &cnt, used)) {
            for (i = p - 1; i < q; i++) printf("%c", s[i]);
            printf("\n");
        } else printf("impossible\n");
    }
    return 0;
}
