from itertools import product

def get_operation_sequence(t1, t2, t3, t4, t5, t6, p, q):
    max_sum = t1 + t2 + t3 + t4 + t5 + t6
    if p > q or p > max_sum or q > max_sum:
        return "impossible"

    directions = ['N', 'S', 'E', 'W']
    for seq in product(directions, repeat=max_sum):
        die = [0, 0, 0, 0, 0, 0]
        for i, direction in enumerate(seq):
            if direction == 'N':
                die[0] = (die[0] + 1) % 6
            elif direction == 'S':
                die[1] = (die[1] + 1) % 6
            elif direction == 'E':
                die[2] = (die[2] + 1) % 6
            else:
                die[3] = (die[3] + 1) % 6
            die[4] = (die[4] + 1) % 6
            die[5] = (die[5] + 1) % 6
        if die == [t1, t2, t3, t4, t5, t6]:
            return ''.join(seq[p-1:q])

    return "impossible"

for _ in range(int(input())):
    t1, t2, t3, t4, t5, t6 = map(int, input().split())
    p, q = map(int, input().split())
    print(get_operation_sequence(t1, t2, t3, t4, t5, t6, p, q))
