#include <iostream>
#include <cmath>

int main() {
    while (true) {
        double v;
        if (!(std::cin >> v)) {
            break;
        }
        if (v <= 0 || v > 200) {
            std::cerr << "Invalid input: velocity should be between 0 and 200" << std::endl;
            continue;
        }
        double t = v / 9.8;
        int n = static_cast<int>(std::sqrt(2 * t * 9.8 / 9.8)) + 1;
        int y = 5 * n - 5;
        std::cout << y << std::endl;
    }
    return 0;
}
