#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 10;
const int M = 5;

int countConfig(const string& boards[N]) {
    int config = 0;
    for (int i = 0; i < (1 << M); ++i) {
        int count[2] = {0, 0};
        for (int j = 0; j < N; ++j) {
            int k = 0;
            for (int l = 0; l < M; ++l) {
                if (boards[j][l] == '1' && (i & (1 << l))) {
                    ++count[0];
                } else if (boards[j][l] == '0' && (i & (1 << l))) {
                    ++count[1];
                }
            }
        }
        if (count[0] == count[1]) {
            ++config;
        }
    }
    return config;
}

int main() {
    string boards[N];
    string line;
    while (getline(cin, line)) {
        if (line == "END") {
            break;
        }
        int i = 0;
        for (const auto& c : line) {
            if (c == ' ') {
                ++i;
            } else {
                boards[i] += c;
            }
        }
        cout << countConfig(boards) << endl;
    }
    return 0;
}
