#include <stdio.h>
#include <math.h>

#define MAX 100

int main() {
    int n, i, j, k, count = 0;
    double x[MAX], y[MAX], r[MAX], area, x1, y1, x2, y2, x3, y3;

    while (scanf("%d", &n) == 1) {
        if (n == 0) break;

        area = 0;
        for (i = 0; i < n; i++) {
            scanf("%lf %lf %lf", &x[i], &y[i], &r[i]);
        }

        for (i = 0; i < n; i++) {
            x1 = x[i] - r[i];
            y1 = y[i] - r[i];
            x2 = x[i] + r[i];
            y2 = y[i] + r[i];

            for (j = 0; j < n; j++) {
                if (i != j) {
                    x3 = x[j] - r[j];
                    y3 = y[j] - r[j];

                    if (x1 < x3 && x3 < x2 && y1 < y3 && y3 < y2) {
                        area += (x3 - x1) * (y3 - y1);
                    }
                }
            }

            area += (x2 - x1) * (y2 - y1);
        }

        printf("%d %.2f\n", ++count, area);
    }

    return 0;
}
