#include <iostream>
#include <vector>
#include <cmath>
#include <iomanip>

using namespace std;

int main() {
    int t;
    cin >> t;
    for (int i = 1; i <= t; i++) {
        int n;
        cin >> n;
        if (n == 0) {
            continue;
        }
        vector<pair<double, double>> antennas(n);
        for (int j = 0; j < n; j++) {
            cin >> antennas[j].first >> antennas[j].second >> antennas[j].second;
        }
        double area = 0;
        for (int j = 0; j < n; j++) {
            double x = antennas[j].first;
            double y = antennas[j].second;
            double r = antennas[j].second;
            area += (2 * x + 2 * y - 2 * r);
        }
        area = (area + 4 * n * 200) / 2;
        cout << i << " " << fixed << setprecision(2) << area << endl;
    }
    return 0;
}
