import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int t = 1;
        while (t-- > 0) {
            int n = scanner.nextInt();
            if (n == 0) break;
            double[][] antennas = new double[n][3];
            for (int i = 0; i < n; i++) {
                antennas[i][0] = scanner.nextDouble();
                antennas[i][1] = scanner.nextDouble();
                antennas[i][2] = scanner.nextDouble();
            }
            double area = 0;
            for (int i = 0; i < n; i++) {
                double x1 = antennas[i][0] - antennas[i][2];
                double y1 = antennas[i][1] - antennas[i][2];
                double x2 = antennas[i][0] + antennas[i][2];
                double y2 = antennas[i][1] + antennas[i][2];
                for (int j = 0; j < n; j++) {
                    if (i != j) {
                        double x3 = antennas[j][0] - antennas[j][2];
                        double y3 = antennas[j][1] - antennas[j][2];
                        double x4 = antennas[j][0] + antennas[j][2];
                        double y4 = antennas[j][1] + antennas[j][2];
                        area += Math.max(Math.max(x1, x2), Math.max(x3, x4)) - Math.min(Math.min(x1, x2), Math.min(x3, x4));
                        area += Math.max(Math.max(y1, y2), Math.max(y3, y4)) - Math.min(Math.min(y1, y2), Math.min(y3, y4));
                    }
                }
            }
            System.out.println((t + 1) + " " + String.format("%.2f", area));
        }
    }
}
