#include <stdio.h>
#include <string.h>
#include <ctype.h>

#define MAX_LINE 1024
#define MAX_WORD 1024

int is_palindrome(char *word) {
    int i, j;
    for (i = 0, j = strlen(word) - 1; i < j; i++, j--) {
        if (tolower(word[i]) != tolower(word[j])) {
            return 0;
        }
    }
    return 1;
}

int main() {
    char line[MAX_LINE];
    char word[MAX_WORD];
    int found[MAX_WORD];
    int num_found = 0;

    while (fgets(line, MAX_LINE, stdin)) {
        int i, j;
        int max_len = 0;
        int max_start = 0;
        int max_end = 0;

        for (i = 0; i < strlen(line); i++) {
            if (isalpha(line[i])) {
                for (j = i; j < strlen(line); j++) {
                    if (isalpha(line[j])) {
                        word[j - i] = line[j];
                        word[j - i + 1] = '\0';
                        if (is_palindrome(word) && strlen(word) > 2) {
                            if (num_found == 0 || strcmp(word, found[num_found - 1]) != 0) {
                                if (strlen(word) > max_len || (strlen(word) == max_len && i < max_start)) {
                                    max_len = strlen(word);
                                    max_start = i;
                                    max_end = j;
                                }
                            }
                        }
                    } else {
                        break;
                    }
                }
            } else {
                break;
            }
        }

        for (i = max_start; i <= max_end; i++) {
            found[num_found++] = line + i;
        }

        for (i = 0; i < num_found; i++) {
            printf("%s", found[i]);
            if (i < num_found - 1) {
                printf(" ");
            }
        }
        printf("\n");

        num_found = 0;
    }

    return 0;
}
