#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
#include <cctype>

using namespace std;

bool isPalindrome(const string& s, int start, int end) {
    while (start < end) {
        if (tolower(s[start]) != tolower(s[end])) {
            return false;
        }
        start++;
        end--;
    }
    return true;
}

int main() {
    string line;
    while (getline(cin, line)) {
        vector<string> palindromes;
        for (int len = 3; len <= line.length(); len++) {
            for (int i = 0; i <= line.length() - len; i++) {
                string substr = line.substr(i, len);
                substr.erase(remove_if(substr.begin(), substr.end(), ::isspace), substr.end());
                substr.erase(remove_if(substr.begin(), substr.end(), ::ispunct), substr.end());
                if (isPalindrome(substr, 0, substr.length() - 1)) {
                    if (palindromes.empty() || substr != palindromes.back()) {
                        palindromes.push_back(substr);
                    }
                }
            }
        }
        sort(palindromes.begin(), palindromes.end());
        for (const auto& palindrome : palindromes) {
            cout << palindrome;
            if (next(palindrome.begin()) != palindrome.end()) {
                cout << " ";
            }
        }
        cout << endl;
    }
    return 0;
}
