#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

int main() {
    int n;
    while (std::cin >> n && n != 0) {
        std::vector<std::string> table(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> table[i];
        }

        std::vector<int> units(n);
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                if (table[i][j] == 'X') {
                    units[j]++;
                }
            }
        }

        int maxConflicts = 0;
        for (int i = 0; i < n; ++i) {
            int conflicts = 0;
            for (int j = 0; j < n; ++j) {
                if (units[j] > j) {
                    conflicts++;
                }
            }
            maxConflicts = std::max(maxConflicts, conflicts);
        }

        int cycles = n * 10;
        int tasks = 0;
        int currentCycle = 0;
        while (tasks < 10) {
            bool conflict = false;
            for (int i = 0; i < n; ++i) {
                if (units[i] > currentCycle) {
                    conflict = true;
                    break;
                }
            }
            if (conflict) {
                currentCycle++;
                continue;
            }
            tasks++;
            for (int i = 0; i < n; ++i) {
                if (table[currentCycle][i] == 'X') {
                    units[i]++;
                }
            }
            currentCycle++;
        }

        std::cout << currentCycle - 1 << std::endl;
    }

    return 0;
}
