import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = 0;
        int count = 0;
        int[][] table = new int[5][7];
        int[] unit = new int[5];
        int[] unitBusy = new int[5];
        int[] task = new int[7];
        int[] taskBusy = new int[7];
        int minCycle = 0;

        while (scanner.hasNextLine()) {
            String line = scanner.nextLine();
            if (line.equals("0")) {
                break;
            }
            if (n == 0) {
                n = Integer.parseInt(line);
                for (int i = 0; i < 5; i++) {
                    unit[i] = 0;
                    unitBusy[i] = 0;
                }
                for (int i = 0; i < 7; i++) {
                    task[i] = 0;
                    taskBusy[i] = 0;
                }
                count = 0;
                minCycle = 0;
            } else {
                String[] rows = line.split(" ");
                for (int i = 0; i < n; i++) {
                    table[count][i] = rows[i].charAt(0) == 'X' ? 1 : 0;
                }
                count++;
            }
        }

        for (int i = 0; i < 10; i++) {
            int cycle = 0;
            while (true) {
                boolean conflict = false;
                for (int j = 0; j < 5; j++) {
                    if (unit[j] > 0 && table[i % n][unit[j] - 1] == 1) {
                        conflict = true;
                        break;
                    }
                }
                if (conflict) {
                    cycle++;
                    for (int j = 0; j < 5; j++) {
                        unit[j]++;
                    }
                } else {
                    for (int j = 0; j < 5; j++) {
                        if (table[i % n][unit[j] - 1] == 1) {
                            unitBusy[j]++;
                        }
                    }
                    for (int j = 0; j < 5; j++) {
                        if (unitBusy[j] > 0) {
                            unit[j]++;
                            unitBusy[j]--;
                        }
                    }
                    cycle++;
                    minCycle = Math.max(minCycle, cycle);
                }
            }
        }
        System.out.println(minCycle);
    }
}
