def min_clock_cycles(reservation_table):
    n = int(reservation_table[0].split()[0])
    units = [list(row) for row in reservation_table[1:]]

    def is_valid_schedule(schedule):
        for i in range(n):
            for j in range(5):
                if schedule[i][j] == 'X' and schedule[(i + 1) % n][j] == 'X':
                    return False
        return True

    def find_min_clock_cycles():
        for i in range(1, 10):
            schedule = [[0] * 5 for _ in range(n)]
            for j in range(i):
                for k in range(n):
                    if units[k][j] == 'X':
                        schedule[(k + j) % n][j] = 'X'
            if is_valid_schedule(schedule):
                return i
        return 10

    return find_min_clock_cycles()


def main():
    while True:
        reservation_table = []
        n = input()
        if n == '0':
            break
        n = int(n.split()[0])
        reservation_table.append(n)
        for _ in range(5):
            reservation_table.append(input())
        print(min_clock_cycles(reservation_table))


if __name__ == "__main__":
    main()
