#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX_POINTS 300

typedef struct {
    int x, y;
} Point;

int compare_points(const void *a, const void *b) {
    Point *p1 = (Point *)a;
    Point *p2 = (Point *)b;
    return (p1->y - p2->y);
}

int main() {
    int n, i, j, k, count[3];
    Point points[MAX_POINTS], q;
    double x, y;

    while (scanf("%d", &n) == 1) {
        if (n == 0) break;

        for (i = 0; i < n; i++) {
            scanf("%d %d", &points[i].x, &points[i].y);
        }

        q.x = 0;
        q.y = 0;

        for (i = 0; i < n; i++) {
            count[0] = count[1] = count[2] = 0;
            for (j = 0; j < n; j++) {
                if (points[j].x == 0 && points[j].y >= points[i].y) {
                    count[0]++;
                } else if (points[j].x == 1000 && points[j].y >= points[i].y) {
                    count[1]++;
                } else if (points[j].x >= points[i].x && points[j].y == 1000) {
                    count[2]++;
                }
            }

            if (count[0] >= n / 3) {
                q.x = (double)points[i].x / 1000;
                q.y = (double)points[i].y / 1000;
                break;
            } else if (count[1] >= n / 3) {
                q.x = (double)(1000 - points[i].x) / 1000;
                q.y = (double)points[i].y / 1000;
                break;
            } else if (count[2] >= n / 3) {
                q.x = (double)points[i].x / 1000;
                q.y = (double)(1000 - points[i].y) / 1000;
                break;
            }
        }

        printf("%.4lf %.4lf\n", q.x, q.y);
    }

    return 0;
}
