#include <iostream>
#include <vector>
#include <algorithm>
#include <iomanip>

struct Point {
    int x, y;
};

struct Line {
    int x1, y1, x2, y2;
};

bool cmpX(const Point& a, const Point& b) {
    return a.x < b.x;
}

bool cmpY(const Point& a, const Point& b) {
    return a.y < b.y;
}

int main() {
    int t;
    std::cin >> t;
    while (t--) {
        int n;
        std::cin >> n;
        if (n == 0) break;
        std::vector<Point> points(n);
        for (int i = 0; i < n; i++) {
            std::cin >> points[i].x >> points[i].y;
        }
        std::sort(points.begin(), points.end(), cmpX);
        std::sort(points.begin(), points.end(), cmpY);
        double qx = (points[0].x + points[n / 3].x + points[2 * n / 3].x) / 3.0;
        double qy = (points[0].y + points[n / 3].y + points[2 * n / 3].y) / 3.0;
        std::cout << std::fixed << std::setprecision(4) << qx << " " << qy << std::endl;
    }
    return 0;
}
